import React, { useState } from 'react';

function CreateProjectBox() {
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    if (selectedImage) {
      setImage(selectedImage);
    }
  };

  const handleSubmit = async () => {
    try {
      const formData = new FormData();
      formData.append('longText', description);
      formData.append('image', image);

      console.log('FormData:', formData);
      console.log('Description:', description);
      console.log('Image:', image);

      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        console.log('Description and image sent successfully.');
        // Clear the description and image
        setDescription('');
        setImage(null);
      } else {
        console.error('Failed to send data to the server.');
        // Handle the error accordingly
      }
    } catch (error) {
      console.error('An error occurred:', error);
      // Handle the error accordingly
    }
  };

  return (
    <div className="create-project-box">
      <h2>Create New Project</h2>
      <div className="input-container">
        <textarea
          rows="4"
          cols="50"
          placeholder="Project Description"
          value={description}
          onChange={handleDescriptionChange}
        />
      </div>
      <div className="input-container">
        <input type="file" accept="image/*" onChange={handleImageChange} />
      </div>
      {image && <img src={URL.createObjectURL(image)} alt="Project Preview" className="image-preview" />}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default CreateProjectBox;
