import React, { useState } from 'react';



const ProjectForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    if (selectedImage) {
      const reader = new FileReader();
      reader.onload = () => {
        setImage(reader.result);
      };
      reader.readAsDataURL(selectedImage);
    }
  };

  const handleSubmit = async () => {
    try {
        console.log(title,description)
      // Submit project information
      const projectResponse = await fetch('http://localhost:5000/api/saveProject', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title, description }),
      });

      if (!projectResponse.ok) {
        console.error('Failed to submit project information.');
        return;
      }

      // Submit image
      if (image) {
        const formData = new FormData();
        formData.append('image', image);
        console.log(image)

        const imageResponse = await fetch('http://localhost:5000/api/upload', {
          method: 'POST',
          body: formData,
        });

        if (imageResponse.ok) {
          const imageData = await imageResponse.json();
          console.log('Image uploaded successfully to Cloudinary. URL:', imageData.url);
        } else {
          console.error('Failed to upload image to Cloudinary.');
        }
      }

      // Clear form fields
      setTitle('');
      setDescription('');
      setImage(null);

      console.log('Project and image information submitted successfully.');
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  return (
    <div className="centered-form">
      <div className="project-form-container">
        <h2>Project Information</h2>
        <div className="input-container">
          <label>Title:</label>
          <input type="text" value={title} onChange={handleTitleChange} />
        </div>
        <div className="input-container">
          <label>Description:</label>
          <textarea
            rows="4"
            cols="50"
            placeholder="Enter project description"
            value={description}
            onChange={handleDescriptionChange}
          />
        </div>
        <div className="input-container">
          <label>Image:</label>
          <input type="file" accept="image/*" onChange={handleImageChange} />
        </div>
        <div className="submit-button-container">
          <button onClick={handleSubmit}>Submit Project Information</button>
        </div>
      </div>
    </div>
  );
};

export default ProjectForm;
