import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      phoneNumber: '',
      email: '',
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();

    const formData = {
      name: this.state.name,
      phoneNumber: this.state.phoneNumber,
      email: this.state.email,
    };

    // Send the form data to the server using fetch or Axios
    fetch('/api/insertRow', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then(response => response.json())
      .then(data => {
        // Handle the response from the server, e.g., show a success message or update the UI.
        console.log(data);
      })
      .catch(error => {
        // Handle any errors, e.g., display an error message to the user.
        console.error(error);
      });
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input type="text" name="name" placeholder="Name" onChange={this.handleChange} />
        <input type="text" name="phoneNumber" placeholder="Phone Number" onChange={this.handleChange} />
        <input type="email" name="email" placeholder="Email" onChange={this.handleChange} />
        <button type="submit">Submit</button>
      </form>
    );
  }
}

export default Form;
