import React, { useState } from 'react';
import './ProjectDescriptionForm.css'; // Import your CSS file for styling

const ProjectDescriptionForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/upload-description', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title, description }),
      });

      if (response.ok) {
        console.log('Project information submitted successfully.');
        // Clear the title and description fields if needed
        setTitle('');
        setDescription('');
      } else {
        console.error('Failed to submit project information.');
        // Handle the error accordingly
      }
    } catch (error) {
      console.error('An error occurred:', error);
      // Handle the error accordingly
    }
  };

  return (
    <div className="centered-form">
      <div className="project-form-container">
        <h2>Project Information</h2>
        <div className="input-container">
          <label>Title:</label>
          <input type="text" value={title} onChange={handleTitleChange} />
        </div>
        <div className="input-container">
          <label>Description:</label>
          <textarea
            rows="4"
            cols="50"
            placeholder="Enter project description"
            value={description}
            onChange={handleDescriptionChange}
          />
        </div>
        <div className="submit-button-container">
          <button onClick={handleSubmit}>Submit Project Information</button>
        </div>
      </div>
    </div>
  );
};

export default ProjectDescriptionForm;
